mapper.select:
SELECT NO , CT , NAME FROM HH_CATEGORY WHERE 1=1
AND CT = #{cType, typeHandler=com.utime.household.config.handler.ECategoryTypeTypeHandler}
ORDER BY CT, NAME

mapper.insert:
INSERT INTO HH_CATEGORY (
	CT
	, NAME
) VALUES (
	#{cType, typeHandler=com.utime.household.config.handler.ECategoryTypeTypeHandler}
	, #{name}
)

mapper.update:
UPDATE HH_CATEGORY SET
	CT = #{cType, typeHandler=com.utime.household.config.handler.ECategoryTypeTypeHandler}
	, NAME = #{name}
WHERE NO = #{no}
