INSERT INTO HH_RECORD (
	DEAL_DATE 
	, AMOUNT 
	, INCLUDED
	, IN_OUT 
	, BANK_CARD_NO 
	, CATEGORY_NO 
	, STORE_NO
	, CONTENT

	, DESCRIPTION

	,HASH
) VALUES (
	DATE(#{dealDate, typeHandler=com.utime.household.dataIO.handler.DealDateTypeHandler})
	, #{amount}
	, #{included, typeHandler=com.utime.household.dataIO.handler.IncludedTypeHandler}
	, #{io, typeHandler=com.utime.household.dataIO.handler.InoutTypeHandler}
	, #{bcVo.no}
	, #{category.no}
	, #{store.no}
	, #{store.store}

	, #{dscr}

	,{hash}
)

mapper.select:
SELECT HR.NO ,HR.REG_DATE ,HR.DEAL_DATE ,HR.AMOUNT ,HR.IN_OUT ,HR.BANK_CARD_NO ,HBC.BC ,HBC.NAME AS BC_NAME ,HR.CATEGORY_NO ,HC.CT ,HC.NAME AS CATEGORY_NAME ,HR.STORE_NO ,HUS.STORE ,HR.CONTENT ,HR.DESCRIPTION ,HR.OWNER_RECORD_NO FROM HH_RECORD HR INNER JOIN HH_BANK_CARD HBC ON HR.BANK_CARD_NO = HBC.NO INNER JOIN HH_CATEGORY HC ON HR.CATEGORY_NO = HC.NO LEFT OUTER JOIN HH_USAGE_STORE HUS ON HR.STORE_NO = HUS.NO AND HUS.CATEGORY_NO = HC.NO WHERE 1=1 AND HR.ENABLED = 'Y' AND HR.INCLUDED = 'T'
AND HR.BANK_CARD_NO = #{bcNo}
AND HR.CATEGORY_NO = #{categoryNo}
AND HR.STORE_NO = #{storeNo}
AND HR.AMOUNT >= <= #{amount}
AND HR.DEAL_DATE BETWEEN DATE(#{begin}) AND DATE(#{end}, '+1 DAY')
AND ( HR.CONTENT LIKE '%${content}%' OR HR.DESCRIPTION LIKE '%${content}%' )
ORDER BY HR.DEAL_DATE DESCASC ,HR.NO DESCASC
