mapper.select:
SELECT ST.NO , ST.STORE AS NAME , ST.CATEGORY_NO , CT.NAME AS CATEGORY_NAME , ST.SUB_CATEGORY_NO , SB.NAME AS SUB_CATEGORY_NAME FROM HH_USAGE_STORE ST INNER JOIN HH_CATEGORY CT ON ST.CATEGORY_NO = CT.NO INNER JOIN HH_SUB_CATEGORY SB ON ST.SUB_CATEGORY_NO = SB.NO WHERE 1=1 AND ST.ENABLED = 'Y'
AND ST.CATEGORY_NO = #{categoryNo}
AND ST.SUB_CATEGORY_NO = #{categorySubNo}
ORDER BY ST.CATEGORY_NO, ST.STORE

mapper.update:
UPDATE HH_USAGE_STORE SET
	CATEGORY_NO = #{categoryNo}
	,SUB_CATEGORY_NO = #{categorySubNo}
	,STORE = #{name}
WHERE NO = #{no}

UPDATE HH_USAGE_STORE SET
	ENABLED = 'N'
WHERE NO = #{no}
